import React, { useState } from "react";
import styled from "styled-components";
import {ReactComponent as Arrow} from "../arrow-long.svg"
import {useRef, useEffect, ueeState} from "react";
import {Timeline, Tween,gsap} from 'gsap';
import './Button.css'
function Button(props) {
    const Button = styled.button`
        color:white;
        font-size: .7em;
        font-weight:500;
        padding: 13px 20px;
        border:none;
        margin: 10px 0px;
        cursor: pointer;
        background: #fc4a1a;  /* fallback for old browsers */
        background: -webkit-linear-gradient(to left, #f7b733, #fc4a1a);  
        background: linear-gradient(to left, #f7b733, #fc4a1a); 
        z-index:2;
        `;

    const [HandleHover, setHandleHover] = useState();
    const [tl] = useState(new gsap.Timeline({ paused: true }));

    useEffect(() => {
        setHandleHover(
          gsap.Tween.to(buttonRef, 1, {
            x: 25,
          }).pause()
        );
      },[])

    const Direction = props.direction;
    console.log(Direction);
    const buttonRef = useRef(null);
    return (
        <div className="button" id="button">
            <Button onMouseEnter={HandleHover.play()} onMouseLeave={HandleHover.reverse()} onClick={toAbout}
            ref={element => {
                buttonRef = element;
            }}>
                {props.children}
            </Button>   
            <svg className={`${Direction} arrow`} width="38" height="10" viewBox="0 0 38 10" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M33.021 6L29.667 8.59L31.5 10L38 5L31.5 0L29.667 1.41L33.021 4H12V6H33.021Z M0 4H12V6H0V4Z" fill="white"/>
            </svg>
        </div>
    )
}
