import './Contact.css';
import React, { useState } from "react";
const Contact = (props) =>{
    const [status, setStatus] = useState("Submit");
  
    return (
        <div className='topography-pattern contact'>
                <p className="hero-header"><em className="name">Contact Me</em></p>
                <p className="hero-paragraph">I spend my days doing <em className="hero-emphasis">Bioinformatics</em> and <em className="hero-emphasis">Full-Stack Development</em> <br></br> On my nights I work on <em className="hero-emphasis">content creation</em> and practice my <em className="hero-emphasis">graphic design</em> skills</p>
                <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="name">Name:</label>
        <input type="text" id="name" required />
      </div>
      <div>
        <label htmlFor="email">Email:</label>
        <input type="email" id="email" required />
      </div>
      <div>
        <label htmlFor="message">Message:</label>
        <textarea id="message" required />
      </div>
      <button type="submit">{status}</button>
    </form>
        </div>
        
    );
  }
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setStatus("Sending...");
    const { name, email, message } = e.target.elements;
    let details = {
      name: name.value,
      email: email.value,
      message: message.value,
    };
    let response = await fetch("http://localhost:5000/contact", {
      method: "POST",
      headers: {
        "Content-Type": "application/json;charset=utf-8",
      },
      body: JSON.stringify(details),
    });
    setStatus("Submit");
    let result = await response.json();
    alert(result.status);
  }
  export default Contact;
