import React,{useRef,useEffect} from 'react'
import { Link } from 'wouter';
import gsap from 'gsap';
import Navlink from './Navlink'
import './Drawer.css'
import Navbar from './Navbar';

function Drawer(props) {
  const barOneRef = useRef();
  const barTwoRef = useRef();
  const barThreeRef = useRef();
  const barFourRef = useRef();
    let drawerMenu = useRef(null);
    let drawerMenuOverlay = useRef(null);
    let drawerMenuLayer = useRef(null);
    const drawerMenuTimeline = useRef();
    console.log(props);
    const { menuState, setMenuState } = props;
  
    useEffect(() => {
      drawerMenuTimeline.current = gsap.timeline({ paused: true });
      drawerMenuTimeline.current.fromTo(
        [drawerMenuOverlay, drawerMenuLayer, drawerMenu],
        {
          duration: 0,
          x: '100%',
        },
        {
          duration: 0.75,
          x: '0%',
          ease: 'power3.inOut',
          stagger: {
            amount: 0.2
          }
        }
      );
    }, []);
  
    useEffect(() => {
      menuState ? drawerMenuTimeline.current.play() : drawerMenuTimeline.current.reverse();
    }, [menuState]);
    function handleClick(){
      setMenuState(!menuState);
      console.log(props);
  }
  useEffect(() => {
    const animation = gsap.to(barOneRef.current, {
          
      y:-120,
      paused: true,
      duration: 0.2,
      
    });
    const animation2 = gsap.to(barTwoRef.current, {
      y:-120,
      paused: true,
      duration: 0.2,
    });
    return () => {
      animation.kill();
      animation.scrollTrigger.kill();
    };
  },[])
    return(
        <>
        <div
        className="drawerNavigationOverlay"
        ref={el => (drawerMenuOverlay = el)}
        >
        </div>
        <div className="drawer">
            <div className="menu-layer" ref={el => (drawerMenuLayer = el)}></div>
            <nav className="drawerNavigation" ref={el => (drawerMenu = el)}>
            <ul className="drawer-list">
                <li className='drawer-item' 
                >
                <Navlink 
                    to='/about' 
                    target="#section-2"
                    className='drawer-links'
                    menuState={menuState} setMenuState={setMenuState}
                >
                    About
                </Navlink>
                <div ref={barOneRef} className="drawer-bar"></div>
                </li>
                <li className='drawer-item'>
                <Navlink
                    to='/work'
                    target="#section-1"
                    className='drawer-links'
                    menuState={menuState} setMenuState={setMenuState}
                >
                    Work
                </Navlink>
                <div ref={barTwoRef} className="drawer-bar"></div>
                </li>
                <li className='drawer-item'>
                <Navlink
                    to='/experiments'
                    className='drawer-links'
                    menuState={menuState} setMenuState={setMenuState}
                >
                    Experiments
                </Navlink>
                <div ref={barThreeRef} className="drawer-bar"></div>
                </li>
    
                <li className='drawer-item'>
                <Navlink
                    to='/contact'
                    target="#section-3"
                    className='drawer-links'
                    menuState={menuState} setMenuState={setMenuState}
                >
                    Contact
                </Navlink>
                <div ref={barFourRef} className="drawer-bar"></div>
                </li>
            </ul>
            </nav>
        </div>
        </>
      );
}

export default Drawer
