import React, { useState,} from 'react';
import { Link } from 'wouter';
import './Navbar.css';
import { Divide as Hamburger } from 'hamburger-react'
const viewportContext = React.createContext({});

const ViewportProvider = ({ children }) => {
  const [width, setWidth] = React.useState(window.innerWidth);
  const [height, setHeight] = React.useState(window.innerHeight);
  const handleWindowResize = () => {
    setWidth(window.innerWidth);
    setHeight(window.innerHeight);
  };

  React.useEffect(() => {
    window.addEventListener("resize", handleWindowResize);
    return () => window.removeEventListener("resize", handleWindowResize);
  }, []);

  return (
    <viewportContext.Provider value={{ width, height }}>
      {children}
    </viewportContext.Provider>
  );
};
const useViewport = () => {
  const [width, setWidth] = React.useState(window.innerWidth);

  React.useEffect(() => {
    const handleWindowResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", handleWindowResize);
    return () => window.removeEventListener("resize", handleWindowResize);
  }, []);

  // Return the width so we can use it in our components
  return { width };
}
const DrawerNav = () => 
  <p>"Hmmm... Why is your screen so small?"</p>
;

const DesktopNav = () => 
<nav className='navbar'>  
  <div className='navbar-container'>
    <Link to='/' className='navbar-logo' onClick={closeMenu}>
      COLLIER DEV
    </Link>
    <ul className={'nav-menu'}>
      <li className='nav-item'>
        <Link to='/about' className='nav-links' onClick={closeMenu}>
          About
        </Link>
      </li>
      <li className='nav-item'>
        <Link
          to='/work'
          className='nav-links'
          onClick={closeMenu}
        >
          Work
        </Link>
      </li>
      <li className='nav-item'>
        <Link
          to='/experiments'
          className='nav-links'
          onClick={closeMenu}
        >
          Experiments
        </Link>
      </li>

      <li className='nav-item'>
        <Link
          to='/contact'
          className='nav-links'
          onClick={closeMenu}
        >
          Contact
        </Link>
      </li>
    </ul>
    <div className='burger'>
    <Hamburger toggled={isMenuOpen} toggle={setIsMenuOpen} size={25} color="#F3742A" position="absolute"/>
    </div>
    
  </div>
</nav>
;

const Navigation = () => {
  const { width } = useViewport();
  const breakpoint = 960;

  return width < breakpoint ? <DrawerNav /> : <DesktopNav />;
};

function Navbar(props) {
  const { width } = useViewport();
  const breakpoint = 620;

  const closeMenu = () => setIsMenuOpen(false);
  const {
    isMenuOpen: [isMenuOpen, setIsMenuOpen]
  } = {
    isMenuOpen: useState(false),
    ...(props.state || {})
  };
  return (
    <ViewportProvider>
      <Navigation />
    </ViewportProvider>
  );
}

export default Navbar;
