import React, {useEffect,useRef,forwardRef}from 'react';
import Skill from './Skill';
import './Skillbar.css';
import Images from '../images/Images.js';
import {skillsReveal} from '../Animantion/Animation';
function SkillCardList(props) {
    let iconRef = useRef();
    let titleRef = useRef();
    let barRef = useRef();
    let listRef = useRef();
    let cardRef = useRef();
    useEffect(() => {
      skillsReveal(cardRef,iconRef,titleRef,barRef,listRef);
    },[]);

    const listIcons = Images.map((image,index) =>(
        <li className="SkillContainer" ref={elem => {cardRef = elem; }} key={index}>
            <Skill refs={iconRef,titleRef,barRef,listRef} desc={image.description} src={image.src} id={image.id} />
        </li>
    ));

    return(
        <ul className="Skills-container">{listIcons}</ul>
    )
}


function Skillbar() {

    return (
        <div className="Skillbar">
            <SkillCardList></SkillCardList>
        </div>
    )
}

export default Skillbar
