import { useEffect, useRef } from 'react';
import * as THREE from "three";
import MathEx from 'js-util/MathEx';
function DNA() {

    const mountRef = useRef(null);
    useEffect(() => {
        var width = document.getElementById('container').offsetWidth;
        var height = document.getElementById('container').offsetHeight;
        console.log(width+'x'+height);
        var scene = new THREE.Scene();
        var camera = new THREE.PerspectiveCamera( 75, width/height, 0.1, 1000 );
        var renderer = new THREE.WebGLRenderer( { alpha: true } );
        const clock = new THREE.Clock({
            autoStart: false
          });
        renderer.setSize(width, height);
        
        mountRef.current.appendChild( renderer.domElement );
        
        const renderTarget = new THREE.WebGLRenderTarget();
        const scenePE = new THREE.Scene();
        const cameraPE = new THREE.OrthographicCamera(-1, 1, 1, -1, 1, 2);

        const dnaHelix = new DnaHelix();

        const postEffect = new PostEffect(renderTarget.texture);
        postEffect.createObj();
        scenePE.add(postEffect.obj);


        var geometry = new THREE.BoxGeometry( 1, 1, 1 );
        var material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
        var cube = new THREE.Mesh( geometry, material );
        
        scene.add( cube );
        camera.position.z = 3;
        
        const render = () => {
            const time = clock.getDelta();
        
            dnaHelix.render(time);
        
            // Render the main scene to frame buffer.
            renderer.setRenderTarget(renderTarget);
            renderer.render(scene, camera);
        
            // Render the post effect.
            postEffect.render(time);
            renderer.setRenderTarget(null);
            renderer.render(scenePE, cameraPE);
          };
          const renderLoop = () => {
            render();
            requestAnimationFrame(renderLoop);
          };
        
          renderer.setClearColor(0x000000, 1.0);

          camera.aspect = 3 / 2;
          camera.far = 1000;
          camera.position.set(-110, -75, 45);
          camera.lookAt(new THREE.Vector3(0, 0, 0));
        
          scene.add(dnaHelix);
          clock.start();
          renderLoop();
    
        return () => mountRef.current.removeChild( renderer.domElement);
      }, []);

    return (
        <div ref={mountRef} id="container">
        </div>
    )
}
class PostEffect {
    constructor(texture) {
      this.uniforms = {
        time: {
          type: 'f',
          value: 0,
        },
        texture: {
          type: 't',
          value: texture,
        },
        resolution: {
          type: 'v2',
          value: new THREE.Vector2(),
        },
      };
      
    }
    createObj() {
      // Define Geometry
      const geometry = new THREE.PlaneGeometry(2, 2);
  
      // Define Material
      const material = new THREE.RawShaderMaterial({
        uniforms: this.uniforms,
        vertexShader: require('./glsl/postEffect.vs').default,
        fragmentShader: require('./glsl/postEffect.fs').default,
      });
  
      // Create Object3D
      this.obj = new THREE.Mesh(geometry, material);
      this.obj.name = 'PostEffect';
    }
    resize(x, y) {
      this.uniforms.resolution.value.set(x, y);
    }
    render(time) {
      this.uniforms.time.value += time;
    }
  }
class DnaHelix extends THREE.Points {
    constructor() {
      // Define Geometry
      const geometry = new THREE.BufferGeometry();
  
      // Define attributes of the instancing geometry
      const numHelix = 6000;
      const numLineSpace = 60;
      const numLine = 100;
      const numAmount = numHelix + numLineSpace * numLine;
      const baPositions = new THREE.BufferAttribute(new Float32Array(numAmount * 3), 3);
      const baRadians = new THREE.BufferAttribute(new Float32Array(numAmount), 1);
      const baRadiuses = new THREE.BufferAttribute(new Float32Array(numAmount), 1);
      const baDelays = new THREE.BufferAttribute(new Float32Array(numAmount), 1);
      for (var i = 0; i < numHelix; i++) {
        const random = Math.random();
        const diff = {
          x: (Math.random() * 2 - 1) * random * 6,
          y: (Math.random() * 2 - 1) * random * 6,
          z: (Math.random() * 2 - 1) * random * 6,
        };
        baPositions.setXYZ(
          i,
          ((i / numHelix) * 2 - 1) * 150 + diff.x,
          diff.y,
          diff.z
        );
        baRadians.setX(i, MathEx.radians(i / numHelix * 900 + i % 2 * 180));
        baRadiuses.setX(i, 18);
        baDelays.setX(i, MathEx.radians(Math.random() * 360));
      }
      for (var j = 0; j < numLineSpace; j++) {
        const radians = MathEx.radians(j / numLineSpace * 900);
        for (var k = 0; k < numLine; k++) {
          const index = j * numLine + k + numHelix;
          const random = Math.random();
          const diff = {
            x: (Math.random() * 2 - 1) * random * 1,
            y: (Math.random() * 2 - 1) * random * 1,
            z: (Math.random() * 2 - 1) * random * 1,
          };
          baPositions.setXYZ(
            index,
            ((j / numLineSpace) * 2 - 1) * 150 + diff.x,
            diff.y,
            diff.z
          );
          baRadians.setX(index, radians);
          baRadiuses.setX(index, (k / numLine * 2 - 1) * 18);
          baDelays.setX(index, MathEx.radians(Math.random() * 360));
        }
      }
      geometry.setAttribute('position', baPositions);
      geometry.setAttribute('radian', baRadians);
      geometry.setAttribute('radius', baRadiuses);
      geometry.setAttribute('delay', baDelays);
  
      // Define Material
      const material = new THREE.RawShaderMaterial({
        uniforms: {
          time: {
            type: 'f',
            value: 0
          },
        },
        vertexShader: require('./glsl/DnaHelix.vs').default,
        fragmentShader: require('./glsl/DnaHelix.fs').default,
        transparent: true,
        blending: THREE.AdditiveBlending,
        depthWrite: false,
      });
  
      // Create Object3D
      super(geometry, material);
      this.name = 'DNA Herix';
    }
    render(time) {
      this.material.uniforms.time.value += time;
    }
  }
export default DNA
