import React, { useState } from "react";
import styled from "styled-components";
import {ReactComponent as Arrow} from "../arrow-long.svg"
import {useRef, useEffect} from "react";
import {gsap} from 'gsap';
import { Timeline } from "gsap/gsap-core";
function AboutButton() {
    const Button = styled.button`
        color:white;
        font-size: .7em;
        font-weight:500;
        padding: 13px 20px;
        border:none;
        margin: 10px 0px;
        cursor: pointer;
        background: #fc4a1a;  /* fallback for old browsers */
        background: -webkit-linear-gradient(to left, #f7b733, #fc4a1a);  
        background: linear-gradient(to left, #f7b733, #fc4a1a); 
        z-index:2;
        `;


    return (
        <div className="button" id="button">
            <Button onMouseEnter={handleHover} onMouseLeave={refreshButton} onClick={toAbout}>
                
                About Me
            </Button>   
            <svg width="70" height="45" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M18.17 13L15.59 15.59L17 17L22 12L17 7L15.59 8.41L18.17 11H2V13H18.17Z" fill="white"/>
            </svg>


        </div>
    )
}
function handleHover(){
    gsap.timeline().to("#button",{x:25});
}

function refreshButton(){
    gsap.timeline().to("#button",{x:0});
}
function toAbout(){
    
}

export default AboutButton;
