import React, {useEffect,useRef} from 'react'
import {skillCardContentReveal} from '../Animantion/Animation';

const Skill = (props) => {
    let iconRef = useRef();
    let titleRef = useRef();
    let barRef = useRef();
    let listRef = useRef();
    const listItems = props.desc.map((item,index) =>(
        <li className="SkillList" key={index}>
            <p>{item}</p>
        </li>
    ));
useEffect(() => {
    skillCardContentReveal(iconRef,titleRef,barRef,listRef);
}, [])

    return (
        <div className="Skill" id={props.id}>
            <img className="SkillIcon" ref={elem => {iconRef = elem; }} src={props.src} alt="skill"/>
            <h3 ref={elem => {titleRef = elem; }}>{props.id}</h3>
            <div className="SkillBar" ref={elem => {barRef = elem; }}></div>
            <ul className="Skill-Item-container" ref={elem => {listRef = elem; }}>{listItems}</ul>
        </div>
    )
};

export default Skill;
