import gsap from "gsap"
import { ScrollTrigger } from "gsap/ScrollTrigger";

export const heroTextReveal = (title,subtitle) => { 
    gsap.from(title, { xPercent: -7.5, opacity: 0, stagger: 0.1, duration: 2, ease: "sine", });
    gsap.from(subtitle, { xPercent: -5, opacity: 0, stagger: 0.2, duration: 2, ease: "sine", });
 };
