import Hero from './components/Hero/Hero'
import About from './components/About/About'
import Work from './components/Work/Work'
import Contact from './components/Contact/Contact'
import Navbar from './components/Navbar/Navbar';

import './App.css';
import React, {useState, useCallback, useRef,useEffect} from 'react'
import gsap from 'gsap'

import { ScrollToPlugin } from "gsap/dist/ScrollToPlugin";
gsap.registerPlugin(ScrollToPlugin);

function App() {
  gsap.registerPlugin(ScrollTrigger); 
  const navRef = useRef();
  let [menuState, setMenuState] = useState(false);
  useEffect(() => {
        tweenRef.current = gsap.from(navRef, { 
            yPercent: -100,
            paused: true,
            duration: 0.2,
            scrollTrigger: {
              trigger: element.querySelector(".navbar"),
              start: "top top",
              end: "bottom center",
              scrub: true
            }
        });
        tweenRef.current = gsap.to(navRef, {
            x: -20,
            paused: true
        });
  })

  return (
    <div className="App">
      <Navbar ref={ref} menuState={menuState} setMenuState={setMenuState} />

      <Hero id="section-0"></Hero>
      <Work id="section-1"></Work>
      <About id="section-2"></About>
      <Contact id="section-3"></Contact>
    </div>
  );
}


export default App;
