import './Work.css';
import Button from '../Button/Button'
import Code from '../images/Code.JPG'
import Nextclade from '../images/Nextclade.JPG'
import T1D from '../images/T1D.jpg'
import Covidfem from '../images/fem-covid.jpg'
import React, {useRef, useEffect} from 'react';
import gsap from 'gsap';
  function Work(props) {
    const onHover = () => {
     cardAnimation2.play();
    };
    const onLeave = () => {
      cardAnimation2.reverse();
     };
    let portfolioRef1 = useRef();
    let portfolioRef2 = useRef();
    let portfolioRef3 = useRef();
    let portfolioRef4 = useRef();
    const tweenRef = useRef();
    const cardAnimation1 =  gsap.fromTo(portfolioRef1.current,{backgroundSize:"150% 150%"}, {backgroundSize:"100% 100%", duration: 2 , paused: true});
    const cardAnimation2 =  gsap.fromTo(portfolioRef2.current,{backgroundSize:"150% 150%"}, {backgroundSize:"100% 100%", duration: 2 , paused: true});
    const cardAnimation3 =  gsap.fromTo(portfolioRef3.current,{backgroundSize:"150% 150%"}, {backgroundSize:"100% 100%", duration: 2 , paused: true});
    const cardAnimation4 =  gsap.fromTo(portfolioRef4.current,{backgroundSize:"150% 150%"}, {backgroundSize:"100% 100%", duration: 2 , paused: true});

    const data = {
      "portfolio-works":[
        {"title":"Personal Portfolio", "description":"Designed in Cinema 4d, Illustrator and Figma. Written in React using Express and MongoDB",  "buttons": [{ "href": "https://github.com/collierdev/Personal_Portfolio", "title": "Visit Project"}], "background": "Code"},
        {"title":"COVID-19 Variant Analysis", "description":"Bash - GATK - BWA - NextClade - Pangolin", "buttons": [{ "href": "https://github.com/collierdev/COVID-19_Variant_Analysis_and_Visualization", "title": "Visit Project"}], "background": "Nextclade"},
        {"title":"Type 1 Diabietes Three Country Cohort Analysis", "description":"R - Kraken - Bracken - Krona - Phyloseq - AmpVis", "buttons": [{ "href": "https://github.com/collierdev/T1D_Diabimmune_Three_Country_Cohort_Analysis", "title": "Visit Project"}, {"href": "/Krona/G69230_krona.html", "title": "View Krona"}], "background": "T1D"},
        {"title":"COVID-19 Female Differnetial Analysis", "description":"R - DESeq - STAR Alignment - Gprofiler - EnrichR", "buttons": [{ "href": "https://github.com/collierdev/COVID-19_Infected_Female-Differential_Analysis", "title": "Visit Project"}], "background": "Covidfem"},
      ]
      }
    

    

    return (
      <>
        <div className='work' id={props.id}>
          <div className="work-header">
            <h2 className="pre-title">Portfolio</h2>
            <h3 className="title">Selected Works</h3>
          </div>
          {data.portfolio-works.map((portfolioData) => {
								console.log(portfolioData);
								return (
									<PortfolioCard title={portfolioData.title} description={portfolioData.description} button={portfolioData.button} background={portfolioData.background}></PortfolioCard>
								);
							})}
          
          <div className="web-portfolio">
              <div className="portfolio-card" onMouseOver={cardAnimation1.play()} onMouseLeave={cardAnimation1.reverse()} ref={portfolioRef1} style={{ backgroundImage: `linear-gradient(90deg, #0A1969 0%, rgba(15, 44, 194, 0.64) 45.19%, rgba(9, 12, 88, 0) 100%), linear-gradient(0deg, rgba(12, 18, 54, 0.41), rgba(12, 18, 54, 0.41)), url(${Code})`, backgroundSize: 'cover', backgroundBlendMode: `normal, darken, normal`}}>
                <h1>Personal Portfolio</h1>
                <h2>Designed in Cinema 4d, Illustrator and Figma.
Written in React using Express and MongoDB</h2>
                <Button direction="left">
                  <a href="https://github.com/collierdev/Personal_Portfolio"> Visit Project </a>
                </Button>
              </div>
              <div className="portfolio-card" onMouseOver={onHover} onMouseLeave={onLeave} ref={portfolioRef2}  style={{ backgroundImage: `linear-gradient(90deg, #0A1969 0%, rgba(15, 44, 194, 0.64) 45.19%, rgba(9, 12, 88, 0) 100%), linear-gradient(0deg, rgba(12, 18, 54, 0.41), rgba(12, 18, 54, 0.41)), url(${Nextclade})`, backgroundSize: 'cover', backgroundBlendMode: `normal, darken, normal`}}>
                <h1>COVID-19 Variant Analysis</h1>
                <h2>Bash - GATK - BWA - NextClade - Pangolin</h2>
                
                <Button direction="left">
                  <a href="https://github.com/collierdev/COVID-19_Variant_Analysis_and_Visualization"> Visit Project </a>
                </Button>
              </div>
              <div className="portfolio-card" ref={portfolioRef3} style={{ backgroundImage: `linear-gradient(90deg, #0A1969 0%, rgba(15, 44, 194, 0.64) 45.19%, rgba(9, 12, 88, 0) 100%), linear-gradient(0deg, rgba(12, 18, 54, 0.41), rgba(12, 18, 54, 0.41)), url(${T1D})`, backgroundSize: 'cover', backgroundBlendMode: `normal, darken, normal`}}>
                <h1>Type 1 Diabietes Three Country Cohort Analysis </h1>
                <h2> R - Kraken - Bracken - Krona - Phyloseq - AmpVis</h2>
                <div className='buttons'>
                <Button direction="left">
                  <a href="https://github.com/collierdev/T1D_Diabimmune_Three_Country_Cohort_Analysis"> Visit Project </a>
                </Button>
                <Button direction="left">
                  <a href="/Krona/G69230_krona.html"> View Krona </a>
                </Button>
                </div>
              </div>
              <div className="portfolio-card" ref={portfolioRef4} style={{ backgroundImage: `linear-gradient(90deg, #0A1969 0%, rgba(15, 44, 194, 0.64) 45.19%, rgba(9, 12, 88, 0) 100%), linear-gradient(0deg, rgba(12, 18, 54, 0.41), rgba(12, 18, 54, 0.41)), url(${Covidfem})`, backgroundSize: 'cover', backgroundBlendMode: `normal, darken, normal`}}>
                <h1>COVID-19 Female Differnetial Analysis </h1>
                <h2> R - DESeq - STAR Alignment - Gprofiler - EnrichR</h2>
                <Button direction="left">
                  <a href="https://github.com/collierdev/COVID-19_Infected_Female-Differential_Analysis"> Visit Project </a>
                </Button>
              </div>
          </div>
          
        </div>
        
      </>
    );
  }
  
  export default Work;

  function PortfolioCard(props){
    return(
      <>
      <div className="portfolio-card" style={{ backgroundImage: `linear-gradient(90deg, #0A1969 0%, rgba(15, 44, 194, 0.64) 45.19%, rgba(9, 12, 88, 0) 100%), linear-gradient(0deg, rgba(12, 18, 54, 0.41), rgba(12, 18, 54, 0.41)), url(${props.background})`, backgroundSize: 'cover', backgroundBlendMode: `normal, darken, normal`}}>
                <h1>{props.title}</h1>
                <h2>{props.description}</h2>
                {props.buttons.map((buttonData) => {
								console.log(buttonData);
								return (
									<Button direction="left">
                  <a href={buttonData.href}> {buttonData.title} </a>
                </Button>
								);
							})}
                
              </div>
      </>
    )
  }
