import React, {useRef,useEffect} from 'react'
import gsap from "gsap"
import './Drawer.css'
import { Link } from 'wouter';
import { ScrollToPlugin } from "gsap/dist/ScrollToPlugin";

const handleClick = (e) => {
  if(menuState){
    setMenuState(false);
  }
  console.log(props);
    console.log(e)
    e.stopPropagation();
    gsap.to(window, {
      duration: 0.7,
      scrollTo: {
        y: `${props.target}`,
        offsetY: 0
      }
    });
  };gsap.registerPlugin(ScrollToPlugin);
function Navlink(props) {
  const { menuState, setMenuState } = props;
    
    

  
  if(props.type === "mobile"){
    return (
        <>
            <Mobile {...props}></Mobile>
        </>
    );
  } else {
    return(
    <>
            <Link
             to={props.to}
             className={props.class}
             onClick={handleClick}
            >
                {props.children}
            </Link>
            <div className="drawer-bar"></div>
        </>
    );
  }
}

export default Navlink

function Mobile(props){
  let tweenRef = useRef();
  let barRef = useRef();
  useEffect(() => {
    tweenRef.current = gsap.fromTo(barRef,{ scaleX: 0, transformOrigin: "left" }, {scaleX: 1, transformOrigin: "right"});
  })
  const onHover = () =>{
    tweenRef.current.play();
    console.log(tweenRef);
  } 
  const onLeave =() =>{
    tweenRef.current.reverse();
  }
  return (
    <>
        <Link
         to={props.to}
         className={props.class}
         onClick={handleClick}
         onMouseEnter={onHover} onMouseLeave={onLeave}
        >
            {props.children}
        </Link>
        <div ref={el => (barRef = el)} className="drawer-bar"></div>
    </>
)
}
