import React, { useState } from "react";
import styled from "styled-components";
import arrow from "../arrow-long.svg"
import {useRef, useEffect} from "react";
import {gsap} from 'gsap';
import { Timeline } from "gsap/gsap-core";
function AboutButton() {
    const Button = styled.button`
        color:white;
        font-size: .7em;
        font-weight:500;
        padding: 13px 20px;
        border:none;
        margin: 10px 0px;
        cursor: pointer;
        background: #fc4a1a;  /* fallback for old browsers */
        background: -webkit-linear-gradient(to left, #f7b733, #fc4a1a);  
        background: linear-gradient(to left, #f7b733, #fc4a1a); 
        z-index:2;
        `;


    return (
        <div className="button" id="button">
            <Button onMouseEnter={handleHover} onMouseLeave={refreshButton} onClick={toAbout}>
                
                About Me
            </Button>   
            <svg width="24" height="51" viewBox="0 0 24 51" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M13.9985 13C13.9985 11.8954 13.103 11 11.9985 11C10.8939 11 9.99846 11.8954 9.99846 13H13.9985ZM9.99846 49.0003C9.99846 50.1049 10.8939 51.0003 11.9985 51.0003C13.103 51.0003 13.9985 50.1049 13.9985 49.0003H9.99846ZM9.99846 13L9.99846 49.0003H13.9985L13.9985 13H9.99846Z" fill="#252525"/>
                <rect width="24" height="12" transform="translate(0 1)" fill="white"/>
                <path d="M19.7574 13L4.24264 13C2.677 13 1.89293 11.1071 3 10L9.87868 3.12132C11.0503 1.94975 12.9497 1.94975 14.1213 3.12132L21 10C22.1071 11.1071 21.323 13 19.7574 13Z" fill="#252525" stroke="#252525" stroke-width="4" stroke-linecap="round" stroke-linejoin="round"/>
                </svg>

        </div>
    )
}
function handleHover(){
    gsap.timeline().to("#button",{x:25});
}

function refreshButton(){
    gsap.timeline().to("#button",{x:0});
}
function toAbout(){
    
}

export default AboutButton;
