import React, {useEffect,useRef,forwardRef}from 'react';
import './Skillbar.css';
import Images from '../images/Images.js';
import {skillCardReveal,skillCardContentReveal} from '../Animantion/Animation';

function SkillCardList(props) {
    let cardRef = useRef();
    let iconRef = useRef();
    let titleRef = useRef();
    let barRef = useRef();
    let listRef = useRef();

    useEffect(() => {
        skillCardReveal(cardRef);
    },[]);

    useEffect(() => {
        skillCardContentReveal(iconRef,titleRef,barRef,listRef);
    }, [])

    function SkillCards(){

        const skillCards = Images.map((image) =>(
            <li className="SkillContainer" ref={elem => {cardRef = elem; }}>
                <div className="Skill" id={props.id}>
                    <img className="SkillIcon" ref={elem => {iconRef = elem; }} src={image.src} alt="skill"/>
                    <h3 ref={elem => {titleRef = elem; }}>{image.id}</h3>
                    <div className="SkillBar" ref={elem => {barRef = elem; }}></div>
                    <ul className="Skill-Item-container" ref={elem => {listRef = elem; }}>
                        {image.description.map((content) => {
                            return (
                                <li className="SkillList" >
                                    <p>{content}</p>
                                 </li>
                            );
                        })}
                    </ul>
                </div>
            </li>
        ));
        
        return({skillCards});
    }
  

    return(
        <ul className="Skills-container">{skillCards}</ul>
    )
}


function Skillbar() {

    return (
        <div className="Skillbar">
            <SkillCardList></SkillCardList>
        </div>
    )
}

export default Skillbar
